package liangjianchao.com.bwei.week3_deamo2;

import android.os.Bundle;
import android.support.annotation.Nullable;
import android.support.v4.app.Fragment;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import com.google.gson.Gson;

import org.xutils.common.Callback;
import org.xutils.http.RequestParams;
import org.xutils.x;

import java.util.ArrayList;
import java.util.List;

import me.maxwin.view.XListView;

/**
 * Created by lenovo„ÄÅ on 2017/8/14.
 */
public class OneFragment extends Fragment {

    private XListView xlv;
    private List<Data.DataBean.ComicsBean> list;
    private MyAdapter adapter;

    @Nullable
    @Override
    public View onCreateView(LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {
       View view = View.inflate(getActivity(),R.layout.item1,null);

        xlv = (XListView) view.findViewById(R.id.xlistview_item);

        list = new ArrayList<Data.DataBean.ComicsBean>();
        adapter = new MyAdapter(list,getActivity());
        xlv.setAdapter(adapter);
        
        loadadd();
        return view;
    }

    private void loadadd() {
       RequestParams params = new RequestParams("http://api.kkmh.com/v1/daily/comic_lists/0?since=0&gender=0&sa_");
        x.http().get(params, new Callback.CommonCallback<String>() {
            @Override
            public void onSuccess(String result) {
              Gson gson =  new Gson();
              Data data =  (Data)gson.fromJson(result,Data.class);

                list.addAll(data.getData().getComics());
                adapter.notifyDataSetChanged();
            }

            @Override
            public void onError(Throwable ex, boolean isOnCallback) {

            }

            @Override
            public void onCancelled(CancelledException cex) {

            }

            @Override
            public void onFinished() {

            }
        });
    }
}
